mapper.select:
select memo
from t_member
where member_id = #{memberId}

mapper.update:
update t_member
set memo = #{memo}
where member_id = #{memberId}

mapper.insert:

    select member_id
    from t_member
    where nickname = #{nickname}


insert ignore t_member
set nickname = #{nickname}

mapper.select:
select nickname
from t_member
where member_id = #{memberId}
