mapper.update:
update t_monthly_goal
set cost_category = #{costCategory},
    goal_cost = #{goalCost},
    save_cost = #{goalCost},
    not_todo_id = #{notTodoId}
where goal_id = #{goalId}

update t_monthly_goal
set save_cost = save_cost - #{useCost}
where goal_id = #{goalId}

mapper.select:
select not_todo_id
from t_not_todo
where name = #{name}
